import React , {useState} from "react";
import Card from "../UI/Card/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button/Button";

const AddUser = props => {
    const [enteredUsername,setUserName ] = useState("");
    const [enteredAge,setUseAge] = useState('');

    const addUserHandler = (event) => {
        event.preventDefault();
        }

    const userNameChangeHandler =(event) => {
        setUserName(event.target.value);
    }    

    const ageChangeHandler=(event) => {
        setUseAge(event.target.value);
    }
    

    return (<Card className={classes.input}>
        <form onSubmit = {addUserHandler}>
            <label htmlFor="username">UserName</label>
            <input id ="username" type="text" value={userNameChangeHandler}></input>
            <label htmlFor="age">UserName</label>
            <input id ="age" value={ageChangeHandler} type="number"></input>
            <Button type="submit"
            //onClick={}
            >Add User</Button>
        </form>

    </Card>)

};

export default AddUser;
